import { Component } from '@angular/core';

@Component({
  selector: 'app-scanqrcode',
  templateUrl: './scanqrcode.component.html',
  styleUrls: ['./scanqrcode.component.css']
})
export class ScanqrcodeComponent {
  scanQRCode() {

  }
}
<!-- For camera -->

<!-- start -->
<button
  class="bg-green-800 px-3 py-1 rounded text-gray-100 mb-4"
  (click)="action.isStart ? action.stop() : action.start()"
>
  {{ action.isStart ? "Stop Scanning" : "Start Scanning" }}
</button>

<div class="w-full flex justify-center">
  <div class="rounded bg-gray-500 p-1">
    <ngx-scanner-qrcode #action="scanner"></ngx-scanner-qrcode>
  </div>
</div>

<!-- data  -->
<span>{{ action.data.value | json }}</span
><!-- value -->
<span>{{ action.data | async | json }}</span
><!-- async -->

<!-- Loading -->
<p *ngIf="action.isLoading">⌛ Loading...</p>
