import { Component, Input } from '@angular/core';
import { TableHeader } from '../../../interfaces/tableheader';
import { TableRow } from 'src/interfaces/tablerow';

@Component({
  selector: 'app-table',
  templateUrl: './table.component.html',
  styleUrls: ['./table.component.css']
})

export class TableComponent {
  @Input() headers : TableHeader[] = [];
  @Input() rows : TableRow[] = [];
}
<div class="relative shadow-lg rounded-xl overflow-auto border p-2 w-auto">
  <div class="overflow-auto my-3">
    <table class="table-auto w-full">
      <thead>
        <tr>
          <th
            *ngFor="let header of headers"
            class="border-b dark:border-slate-300 font-normal p-4 pl-8 pt-0 pb-3 text-left text-nowrap"
          >
            {{ header.name }}
          </th>
        </tr>
      </thead>

      <tbody>
        <tr *ngFor="let row of rows">
          <td
            *ngFor="let value of row.values"
            class="border-b border-slate-100 dark:border-slate-200 p-4 pl-8 text-slate-600 dark:text-slate-500 text-nowrap"
          >
            {{ value }}
          </td>
        </tr>
      </tbody>
    </table>
  </div>
</div>
