import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'attendance-monitoring-using-qr-code';
  sidebarActive : boolean = false;
  activeSidebarClass: string = "left-0";  
  inactiveSidebarClass: string = "-left-full";  

  protected toggleSidebar(value: boolean) {
    this.sidebarActive = !this.sidebarActive;
  }
}
<div class="h-screen w-full flex flex-col relative">
  <app-topbar
    class="sticky top-0 z-20 shadow-lg"
    (toggleSidebarEvent)="toggleSidebar($event)"
  ></app-topbar>

  <div class="flex w-full h-full">
    <app-sidebar
      class="fixed -left- shadow-lg h-screen z-10 transition-[left] ease-in-out duration-300"
      [ngClass]="[sidebarActive ? activeSidebarClass : inactiveSidebarClass]"
      [sidebarActive]="sidebarActive"
      (toggleSidebarEvent)="toggleSidebar($event)"
    ></app-sidebar>

    <main class="container overflow-hidden p-4">
      <router-outlet></router-outlet>
    </main>
  </div>

  <div
    class="z-0 fixed w-screen h-screen top-0 bg-gradient-to-tl from-green-950 to-lime-800 opacity-60 blur-sm"
    [ngClass]="[sidebarActive ? 'block' : 'hidden']"
    (click)="toggleSidebar(sidebarActive)"
  ></div>
</div>
